glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
  in vec2 a_uv0;
  out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
    pos = cc_matViewProj * cc_matWorld * pos;
  #else
    pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
    v_uv0 = a_uv0;
  #endif
    v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform info {
  vec2 resolution;
  float gametime;
  float amp;
};
vec2 s(vec2 p) {
  float d=gametime * 10.,x=10.*(p.x+d),y=8.*(p.y+d);
  return vec2(cos(x-y)*cos(y),sin(x+y)*sin(y));
}
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec2 rs = resolution;
  vec2 uv = v_uv0;
  vec2 q = uv+amp/resolution.x*(s(uv)-s(uv+rs));
  o *= texture(texture, q);
  o *= v_color;
  gl_FragColor = o;
}

